import React from "react";
import { useForm } from "react-hook-form";

export default function TaskHookForm(props) {
  const {
    register,
    handleSubmit,
    reset,
    watch,
    formState: { errors, isValid },
  } = useForm({ mode: "onChange" });

  const { kisiler, handleTaskSubmit } = props;

  const onSubmit = (data) => {
    handleTaskSubmit(data);
    reset();
  };
  console.log(errors);
  //console.log(watch());
  return (
    <form className="taskForm" onSubmit={(e) => handleSubmit(onSubmit(e))}>
      <div className="form-line">
        <label className="input-label" htmlFor="title">
          Başlık
        </label>
        <input
          className="input-text"
          id="title"
          name="title"
          type="text"
          {...register("title", {
            required: "Task başlığı yazmalısınız",
            minLength: {
              value: 3,
              message: "Task başlığı en az 3 karakter olmalı",
            },
          })}
        />
        {errors.title && <p className="input-error">{errors.title.message}</p>}
      </div>

      <div className="form-line">
        <label className="input-label" htmlFor="description">
          Açıklama
        </label>
        <textarea
          className="input-textarea"
          rows="3"
          id="description"
          name="description"
          {...register("description", {
            required: "Task açıklaması yazmalısınız",
            minLength: {
              value: 10,
              message: "Task açıklaması en az 10 karakter olmalı",
            },
          })}
        ></textarea>
        {errors.description && (
          <p className="input-error">{errors.description.message}</p>
        )}
      </div>

      <div className="form-line">
        <label className="input-label">İnsanlar</label>
        <div>
          {kisiler.map((person) => (
            <label className="input-checkbox" key={person}>
              <input
                type="checkbox"
                name="people"
                value={person}
                {...register("people", {
                  required: "Lütfen en az bir kişi seçin",
                  maxLength: {
                    value: 3,
                    message: "En fazla 3 kişi seçebilirsiniz",
                  },
                })}
              />
              {person}
            </label>
          ))}
        </div>
        {errors.people && (
          <p className="input-error">{errors.people.message}</p>
        )}
      </div>

      <div className="form-line">
        <button className="submit-button" type="submit" disabled={!isValid}>
          Kaydet
        </button>
      </div>
    </form>
  );
}
